#!/bin/bash
set -e # Exit immediately if any simple command fails.
set -u # Treat unset variables as an error.

DB_SOURCE="+DB_SOURCE+"
DST="+DST+"

# When sql files are generated by windows jobs, we sometimes
# need to wait to be sure that the NFS handles are all 
# released so we can read the output files:
sleep 10

sqlite3 "$DB_SOURCE" .dump  > $DST

# not needed
#echo "COMMIT;" >> $DST

#gzip $DST
